import { useField } from "formik";
import {
    StyledTextInput, 
    StyledLablel,
    StyledIcon
} from "./../components/Styles";

export const TextInput = ({icon, ...props}) => {
    const [field, meta] = useField(props);

    return(
        <div styled={{position:"relative"}}>
            <StyledLablel htmlFor={props.name}>
                {props.label}
            </StyledLablel>
            <StyledTextInput
                {...field}
                {...props}
            />
            <StyledIcon>
                {icon}
            </StyledIcon>
        </div>
    )
}
